import React, { useState } from 'react';
const API_BASE = 'http://localhost:4001/todo';

function TodoItem(props) {
  const { name, id, completed, setItems } = props;
  const [isEditing, setIsEditing] = useState(false);
  const [editValue, setEditValue] = useState(name);
  const [isDeleted, setIsDeleted] = useState(false);
  const [undoTimeout, setUndoTimeout] = useState(null);

  const deleteTodo = (id) => {
    setIsDeleted(true);

    // Temporarily remove the task from the UI
    setItems((items) => items.filter((item) => item._id !== id));

    // Set a timer for permanent deletion
    const timeout = setTimeout(async () => {
      try {
        const response = await fetch(`${API_BASE}/delete/${id}`, {
          method: 'DELETE',
        });
        if (!response.ok) {
          throw new Error('Failed to delete a task');
        }
        setIsDeleted(false); // Clear undo state after permanent deletion
      } catch (error) {
        console.error('Error deleting task:', error);
        // Optionally restore the item on error
      }
    }, 3000); // 3-second window to undo

    setUndoTimeout(timeout);
  };

  const undoDelete = () => {
    clearTimeout(undoTimeout); // Cancel the deletion timer
    setIsDeleted(false); // Remove the "deleted" state
    setItems((items) => [...items, { name, _id: id, completed }]); // Re-add the item
  };

  const updateTodo = async () => {
    if (editValue.trim() === '') {
      alert('Task name cannot be empty');
      return;
    }

    try {
      const response = await fetch(`${API_BASE}/update/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: editValue }),
      });
      if (!response.ok) {
        throw new Error('Failed to update task');
      }
      const updatedTodo = await response.json();
      setItems((items) => items.map((item) => (item._id === id ? { ...item, name: updatedTodo.name } : item)));
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  if (isDeleted) {
    // Display the "Undo" message with a button
    return (
      <div className="undo-message">
        <span>Task "{name}" deleted.</span>
        <button onClick={undoDelete}>Undo</button>
      </div>
    );
  }

  return (
    <div className={`todo-item ${completed ? 'check-complete' : ''}`} key={id}>
      {isEditing ? (
        <input
          type="text"
          value={editValue}
          onChange={(e) => setEditValue(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter') updateTodo();
          }}
        />
      ) : (
        <div className="text">{name}</div>
      )}

      <button className="edit-btn" onClick={() => setIsEditing(!isEditing)}>
        ‚úèÔ∏è
      </button>
      <button className="delete-btn" onClick={() => deleteTodo(id)}>
        üóëÔ∏è
      </button>

      {isEditing && (
        <button className="save-btn" onClick={updateTodo}>
          üíæ
        </button>
      )}
    </div>
  );
}

export default TodoItem;
