import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Register from './components/Register';
import Login from './components/Login';
import TodoApp from './components/TodoApp'; // Main Todo app component
import Header from './components/Header'; // Optional header with navigation links

function App() {
  const isAuthenticated = !!localStorage.getItem('token'); // Simple check for authentication

  return (
    <Router>
      {!isAuthenticated && <Header />} {/* Conditionally render Header if needed */}
      <Routes>
        <Route
          path="/register"
          element={!isAuthenticated ? <Register /> : <Navigate to="/" />}
        />
        <Route
          path="/login"
          element={!isAuthenticated ? <Login /> : <Navigate to="/" />}
        />
        <Route
          path="/"
          element={isAuthenticated ? <TodoApp /> : <Navigate to="/login" />}
        />
      </Routes>
    </Router>
  );
}

export default App;
