import { useEffect, useState } from 'react';
import TodoItem from './TodoItem';

const API_BASE = 'http://localhost:4001/todo';

function TodoApp() {
  const [items, setItems] = useState([]);
  const [input, setInput] = useState('');
  const [message, setMessage] = useState(''); // State for displaying messages

  useEffect(() => {
    GetTodos();
  }, []);

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const GetTodos = () => {
    fetch(API_BASE)
      .then((res) => res.json())
      .then((data) => setItems(data))
      .catch((err) => console.log(err));
  };

  const addItem = async () => {
    const trimmedInput = input.trim();

    // Check for duplicate task
    if (items.some((item) => item.name.toLowerCase() === trimmedInput.toLowerCase())) {
      setMessage('Task already exists!');
      setTimeout(() => setMessage(''), 3000); // Clear message after 3 seconds
      return;
    }

    if (trimmedInput === '') {
      setMessage('Task cannot be empty!');
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    try {
      const data = await fetch(API_BASE + '/new', {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
        },
        body: JSON.stringify({
          name: trimmedInput,
          completed: false,
        }),
      }).then((res) => res.json());

      setItems([...items, data.newTask]);
      setInput('');
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <div className="container">
      <div className="heading">
        <h1>TO-DO-APP</h1>
      </div>
      {message && <div className="message">{message}</div>} {/* Display the message if it exists */}
      <div className="form">
        <input type="text" value={input} onChange={handleChange} />
        <button onClick={addItem} disabled={input.trim() === ''}>
          <span>ADD</span>
        </button>
      </div>
      <div className="todolist">
        {items.map((item) => {
          const { _id, name, completed } = item;
          return (
            <TodoItem
              key={_id} // Add the unique key prop here
              name={name}
              id={_id}
              completed={completed}
              setItems={setItems}
            />
          );
        })}
      </div>
    </div>
  );
}

export default TodoApp;
